import React, {useState} from "react";


const AddDentist = ({addContact}) => {
    const [assistant, setDentist] = useState({
            first_name: "",
            last_name: "",
            phone: "",
            email: "",
            isSick: false,
            id: Date.now()
    })

    const onChangeDentist = (e) =>{
            setDentist({...assistant, [e.target.name] : e.target.value})
    }

    const handleSubmit = (e) =>{
            e.preventDefault();
            addContact(assistant);
            setDentist({first_name: "",last_name: "",number: "",email: ""});
    }



   return (
   <div>

           <form onSubmit={handleSubmit}>

           <input 
           type="text"
           required
           value = {assistant.first_name}
           placeholder="name"
           name = "first_name"
           onChange={onChangeDentist}/>

           <input 
           type="text"
           required
           value = {assistant.last_name}
           name="last_name"
           placeholder="surname"
           onChange={onChangeDentist}/>

           <input 
           type="email"
           required
           value = {assistant.email}
           name="email"
           placeholder="email"
           onChange={onChangeDentist}/>

           <input 
           type="number"
           required
           value = {assistant.number}
           name="phone"
           placeholder="phone"
           onChange={onChangeDentist}/>

           <button type="submit">Add Dentist</button>
           </form>

   </div>
   )}


   export default AddDentist
